<script type="text/javascript">
	var _$ = jQuery;
    var ctrlBar = false;
    var float_timer = null;
    var float_max_X;
    var float_max_Y;
    var float_ctrl_X = true;
    var float_ctrl_Y = true;
    _$(function () {
        float_max_X = _$(window).width();
        float_max_Y = _$(window).height();
    });
    function showVideoImage() {
        if (_$('.vjs-poster').css('display') == 'none') {
        _$('.vjs-poster').addClass('index').show();
        } else {
            setTimeout('showVideoImage()', 50);
        }
    }

    function startMove() {
        var obj = _$('#floatAdv');
        var limit_X = float_max_X - obj.width();
        var limit_Y = float_max_Y - obj.height();
        float_timer = setInterval(function () {
            var _x = parseInt(obj.css('left'));
            var _y = parseInt(obj.css('top'));
            if (_x >= limit_X) {
                float_ctrl_X = false;
            }
            if (_x <= 0) {
                float_ctrl_X = true;
            }
            if (_y >= limit_Y) {
                float_ctrl_Y = false;
            }
            if (_y <= 0) {
                float_ctrl_Y = true;
            }
            if (float_ctrl_X) {
                _x += 1;
            } else {
                _x -= 1;
            }
            if (float_ctrl_Y) {
                _y += 1;
            } else {
                _y -= 1;
            }
            obj.css({
                'left': _x + 'px',
                'top': _y + 'px'
            });
        }, 10);
    }

    function endMove() {
        clearInterval(float_timer);
    }

    _$(document).ready(function () {
        _$('.ag-header ul li:eq(0)').addClass('current-page');
        showVideoImage();

        _$('#floatAdv').mouseenter(function () {
            endMove();
        });
        _$('#floatAdv').mouseleave(function () {
            startMove();
        });
        _$('#floatAdv span').click(function (e) {
            endMove();
            _$('#floatAdv').attr('href', 'portal.php?mod=topic&topicid=1').removeAttr('target').hide();
        });

        _$(".ag-content-customer-ele").bind("mouseenter mouseleave", function (e) {
		    var w = _$(this).width();
		    var h = _$(this).height();
		    var x = (e.pageX - this.offsetLeft - (w / 2)) * (w > h ? (h / w) : 1);
		    var y = (e.pageY - this.offsetTop - (h / 2)) * (h > w ? (w / h) : 1);
		    var direction = Math.round((((Math.atan2(y, x) * (180 / Math.PI)) + 180) / 90) + 3) % 4;
		    if (e.type == 'mouseenter') {
                // 0:up - 1:right - 2:down - 3:left
			    if(direction == 0) {
				    _$(this).find('div').css({
					    'top' : '-470px',
					    'left' : '0px'
				    });
				    _$(this).find('div').animate({ 'top': 0 }, { queue: false, duration: 300 });
			    } else if(direction == 2) {
				    _$(this).find('div').css({
					    'top' : '470px',
					    'left' : '0px'
				    });
				    _$(this).find('div').animate({ 'top': 0 }, { queue: false, duration: 300 });
			    } else if(direction == 1) {
				    _$(this).find('div').css({
					    'top' : '0px',
					    'left' : '200px'
				    });
				    _$(this).find('div').animate({ 'left': 0 }, { queue: false, duration: 300 });
			    } else if(direction == 3) {
				    _$(this).find('div').css({
					    'top' : '0px',
					    'left' : '-200px'
				    });
				    _$(this).find('div').animate({ 'left': 0 }, { queue: false, duration: 300 });
			    }
			    _$(this).find('span').css('color', '#fff');
			    _$(this).find('img').animate({ 'left': _$(this).find('img').attr('data-hover') }, { queue: false, duration: 200 });
		    }else{
			    if(direction == 0) {
			        _$(this).find('div').animate({ 'top': -470 }, { queue: false, duration: 300 });
			    } else if(direction == 2) {
			        _$(this).find('div').animate({ 'top': 470 }, { queue: false, duration: 300 });
			    } else if(direction == 1) {
			        _$(this).find('div').animate({ 'left': 200 }, { queue: false, duration: 300 });
			    } else if(direction == 3) {
			        _$(this).find('div').animate({ 'left': -200 }, { queue: false, duration: 300 });
			    }
			    _$(this).find('span').css('color', '#262626');
			    _$(this).find('img').animate({ 'left': _$(this).find('img').attr('data-normal') }, { queue: false, duration: 200 });
		    }
        });

        _$(".ag-content-customer-ele").bind('click', function (e) {

            var navIndex = _$(e.target).parent().index();
            _$('.ag-content-customer-ele-detail ul li').removeClass('current');
            _$('.ag-content-customer-ele-detail ul li').eq(navIndex).addClass('current');

            _$('.ag-content-customer-wrap').css('background-color', '#469acb');
            _$('.ag-content-customer-ele').animate({ 'width': 0 }, 500);

            _$('.ag-content-customer-ele-detail').animate({ 'width': 1199 }, {
                duration: 500,
                complete: function () {
                    _$('.ag-content-customer-ele-detail ul li').eq(navIndex).click();
                }
            });
        });

        _$('.ag-content-customer-ele-detail-return').bind('click', function (e) {

            _$('.ag-content-customer-wrap').css('background-color', '#f1f1f1');
            _$('.ag-content-customer-ele-detail').css('overflow', 'hidden');
            _$('.ag-content-customer-ele').animate({ 'width': 200 }, 500);
            _$('.ag-content-customer-ele-detail').animate({ 'width': 0 }, 500);
            _$('.ag-content-customer-ele-detail-display').hide();
        });

        _$('.ag-content-customer-ele-detail ul li').bind('click', function () {

            _$('.ag-content-customer-ele-detail ul li').removeClass('current');
            _$(this).addClass('current');
            _$('.ag-content-customer-ele-detail').css('overflow', 'visible');

            var disIndex = _$(this).index();
            _$('.ag-content-customer-ele-detail-display').hide();
            _$('.ag-content-customer-ele-detail-display').eq(disIndex).show();

            // IE
            if ("ActiveXObject" in window) {
                _$('.ag-content-customer-ele-detail-display-left').css({
                    'left': '0px',
                    'opacity':'1'
                });
                _$('.ag-content-customer-ele-detail-display-right').css({
                    'right': '-120px',
                    'opacity': '1'
                });
                _$('.ag-content-customer-ele-detail-display-left').eq(disIndex).animate({ 'left': 120 }, { duration: 1000, easing: 'easeOutQuint' });
                _$('.ag-content-customer-ele-detail-display-right').eq(disIndex).animate({ 'right': 0 }, { duration: 1000, easing: 'easeOutQuint' });
            }
        });
    });
</script></div></div></div></div></div></div><!--[/diy]-->
